end);

-- Database
eldermud.db = db:create("eldermud", {
  profile = {
    username = "",
    password = "",
  },
});

registerNamedEventHandler('Eldermud', 'eldermud.component.login', 'gmcp.eldermud.login', function(e, text)
  local res = db:fetch(eldermud.db.profile);
